import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Shopping = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');
  const [category, setCategory] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/products/${category}`);
        setProducts(response.data);
      } catch (err) {
        setError('Failed to fetch products');
      }
    };

    fetchProducts();
  }, [category]);

  const handleCategoryChange = (category) => {
    setCategory(category);
  };

  return (
    <div className="shopping-container">
      <h2>Available Products</h2>
      <nav className="shopping-navbar">
        <ul>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('fruits')}>
              Fruits
            </Link>
          </li>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('vegetables')}>
              Vegetables
            </Link>
          </li>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('grains-and-cereals')}>
              Grains and Cereals
            </Link>
          </li>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('pulses-and-legumes')}>
              Pulses and Legumes
            </Link>
          </li>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('spices-and-herbs')}>
              Spices and Herbs
            </Link>
          </li>
          <li>
            <Link to="#" onClick={() => handleCategoryChange('nuts-and-seeds')}>
              Nuts and Seeds
            </Link>
          </li>
        </ul>
      </nav>
      {error && <p className="error-message">{error}</p>}
      <ul className="product-list">
        {products.map((product) => (
          <li key={product._id}>
            {product.name} - {product.quantity} kg - â‚¹{product.price} per kg
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Shopping;
