import React, { useState } from 'react';
import axios from 'axios';
import '../styles/Sell.css';

const Sell = () => {
  const [formData, setFormData] = useState({
    name: '',
    quantity: '',
    price: '',
    category: '',
    farmerName: '',
    imageUrl: '',
  });
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSubmit = new FormData();
      formDataToSubmit.append('name', formData.name);
      formDataToSubmit.append('quantity', formData.quantity);
      formDataToSubmit.append('price', formData.price);
      formDataToSubmit.append('category', formData.category);
      formDataToSubmit.append('farmerName', formData.farmerName);
      formDataToSubmit.append('imageUrl', formData.imageUrl);

      const response = await axios.post('http://localhost:5000/api/products/add', formDataToSubmit);
      setMessage(response.data.message);
      setFormData({ name: '', quantity: '', price: '', category: '', farmerName: '', imageUrl: '' });
    } catch (error) {
      setError('Error adding product: ' + error.message);
    }
  };

  return (
    <div>
      <h1>Add Product</h1>
      < form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          placeholder="Product Name"
          required
        />
        <input
          type="number"
          name="quantity"
          value={formData.quantity}
          onChange={handleChange}
          placeholder="Quantity (kg)"
          required
        />
        <input
          type="number"
          name="price"
          value={formData.price}
          onChange={handleChange}
          placeholder="Price (â‚¹ per kg)"
          required
        />
        <select
          name="category"
          value={formData.category}
          onChange={handleChange}
          required
        >
          <option value="">Select Category</option>
          <option value="fruits">Fruits</option>
          <option value="vegetables">Vegetables</option>
          <option value="grains">Grains and Cereals</option>
          <option value="pulses">Pulses and Legumes</option>
          <option value="spices">Spices and Herbs</option>
          <option value="nuts">Nuts and Seeds</option>
        </select>
        <input
          type="text"
          name="farmerName"
          value={formData.farmerName}
          onChange={handleChange}
          placeholder="Farmer Name"
          required
        />
        <input
          type="file"
          name="imageUrl"
          onChange={(e) => setFormData({ ...formData, imageUrl: e.target.files[0] })}
          required
        />
        <button type="submit">Add Product</button>
      </form>
      {message && <p>{message}</p>}
      {error && <p>{error}</p>}
    </div>
  );
};

export default Sell;
