// Shopping.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Shopping.css';

const Shopping = () => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState('');
  const [category, setCategory] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const endpoint = category ? `/api/products/${category}` : '/api/products/all';
        const response = await axios.get(`http://localhost:5000${endpoint}`);
        setProducts(category ? response.data : response.data.products);
      } catch (err) {
        setError('Failed to fetch products');
      }
    };

    fetchProducts();
  }, [category]);

  const handleCategoryChange = (category) => {
    setCategory(category);
  };

  const handleAddToCart = async (productId) => {
    try {
      await axios.post('http://localhost:5000/api/cart', { productId, quantity: 1 });
      alert('Product added to cart');
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="shopping-container">
      <nav className="shopping-navbar">
        <ul>
          <li>
            <a href="#fruits" onClick={() => handleCategoryChange('fruits')}>
              Fruits
            </a>
          </li>
          <li>
            <a href="#vegetables" onClick={() => handleCategoryChange('vegetables')}>
              Vegetables
            </a>
          </li>
          <li>
            <a href="#grains" onClick={() => handleCategoryChange('grains')}>
              Grains and Cereals
            </a>
          </li>
          <li>
            <a href="#pulses" onClick={() => handleCategoryChange('pulses')}>
              Pulses and Legumes
            </a>
          </li>
          <li>
            <a href="#spices" onClick={() => handleCategoryChange('spices')}>
              Spices and Herbs
            </a>
          </li>
          <li>
            <a href="#nuts" onClick={() => handleCategoryChange('nuts')}>
              Nuts and Seeds
            </a>
          </li>
          <li>
            <a href="#all" onClick={() => handleCategoryChange('')}>
              All Products
            </a>
          </li>
        </ul>
      </nav>
      <div className="product-sections">
        {products.map((product) => (
          <div key={product._id} className="product-section">
            <h2>{product.name}</h2>
            <p>Quantity: {product.quantity} kg</p>
            <p>Price: â‚¹{product.price} per kg</p>
            <p>Sold by: {product.farmerName}</p>
            <button onClick={() => handleAddToCart(product._id)}>Add to Cart</button>
          </div>
        ))}
      </div>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default Shopping;
