import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Welcome = () => {
  const [showAuthButtons, setShowAuthButtons] = useState(false);
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setShowAuthButtons(true);
    }, 4000);

    return () => clearTimeout(timeoutId);
  }, []);

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
  };

  return (
    <div className="welcome-container">
      <div className={`welcome-message ${showAuthButtons ? 'hidden' : ''}`}>
        <h1>Raita Mitra (ರೈತ ಮಿತ್ರ)</h1>
        <p>"ಹೆಮ್ಮೆಯ ಕೃಷಿಕರ ಬಲವೇ ನಮ್ಮ ರಾಷ್ಟ್ರದ ಶಕ್ತಿ!"</p>
      </div>

      {showAuthButtons && (
        <div className="auth-buttons">
          <Link to="/signin">
            <button>{language === 'en' ? 'Sign In' : 'ಸೈನ್ ಇನ್'}</button>
          </Link>
          <Link to="/signup">
            <button>{language === 'en' ? 'Sign Up' : 'ಸೈನ್ ಅಪ್'}</button>
          </Link>
        </div>
      )}

      {showAuthButtons && (
        <div className="language-toggle">
          <button onClick={() => handleLanguageChange('en')}>English</button>
          <button onClick={() => handleLanguageChange('kn')}>ಕನ್ನಡ</button>
        </div>
      )}
    </div>
  );
};

export default Welcome;
