import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Welcome.css'; // Import the CSS file for styling

const Welcome = () => {
  const [showAuthButtons, setShowAuthButtons] = useState(false);
  const [showLanguageToggle, setShowLanguageToggle] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState('en');
  const navigate = useNavigate();

  useEffect(() => {
    // Show auth buttons and language toggle after 4 seconds
    const timer = setTimeout(() => {
      setShowAuthButtons(true);
      setShowLanguageToggle(true);
    }, 4000);

    return () => clearTimeout(timer); // Cleanup timer
  }, []);

  const setLanguage = (lang) => {
    setCurrentLanguage(lang);
  };

  const handleSignIn = () => {
    navigate('/signin');
  };

  const handleSignUp = () => {
    navigate('/signup');
  };

  return (
    <div className="welcome-container">
      <div className="welcome-message">
        <h1>Raita Mitra (ರೈತ ಮಿತ್ರ)</h1>
        <p>"ಹೆಮ್ಮೆಯ ಕೃಷಿಕರ ಬಲವೇ ನಮ್ಮ ರಾಷ್ಟ್ರದ ಶಕ್ತಿ!"</p>
      </div>

      {showAuthButtons && (
        <div className="auth-buttons">
          <button onClick={handleSignIn}>
            {currentLanguage === 'en' ? 'Sign In' : 'ಸೈನ್ ಇನ್'}
          </button>
          <button onClick={handleSignUp}>
            {currentLanguage === 'en' ? 'Sign Up' : 'ಸೈನ್ ಅಪ್'}
          </button>
        </div>
      )}

      {showLanguageToggle && (
        <div className="language-toggle">
          <button onClick={() => setLanguage('en')}>English</button>
          <button onClick={() => setLanguage('kn')}>ಕನ್ನಡ</button>
        </div>
      )}
    </div>
  );
};

export default Welcome;
