import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Shopping = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/products/all');
        setProducts(response.data.products);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <h2>Available Products</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {products.length === 0 ? (
          <p>No products available.</p>
        ) : (
          products.map((product) => (
            <div key={product._id} style={{ border: '1px solid #ddd', padding: '20px', margin: '10px', borderRadius: '8px' }}>
              <h3>{product.name}</h3>
              <p>Quantity: {product.quantity}</p>
              <p>Price: â‚¹{product.price} per kg</p>
              <p>Sold by: {product.farmerId.name}</p>
              <button style={{ padding: '10px 20px', backgroundColor: '#00796b', color: 'white', border: 'none', borderRadius: '5px' }}>Buy Now</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Shopping;
