import React, { useState } from 'react';
import axios from 'axios';
import "../styles/Sell.css"

const Sell = () => {
  const [formData, setFormData] = useState({ name: '', quantity: '', price: '', category: '' });
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('You need to sign in first');
        return;
      }

      const response = await axios.post('http://localhost:5000/api/products/add', formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setMessage(response.data.message);
      setFormData({ name: '', quantity: '', price: '', category: '' });
    } catch (err) {
      setError(err.response.data.message);
    }
  };

  return (
    <div className="sell-container">
      <h2>Sell Your Products</h2>
      <form className="sell-form" onSubmit={handleSubmit}>
        <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Product Name" required />
        <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} placeholder="Quantity" required />
        <input type="number" name="price" value={formData.price} onChange={handleChange} placeholder="Price per kg" required />
        <select name="category" value={formData.category} onChange={handleChange} required>
          <option value="">Select Category</option>
          <option value="fruits">Fruits</option>
          <option value="vegetables">Vegetables</option>
          <option value="grains-and-cereals">Grains and Cereals</option>
          <option value="pulses-and-legumes">Pulses and Legumes</option>
          <option value="spices-and-herbs">Spices and Herbs</option>
          <option value="nuts-and-seeds">Nuts and Seeds</option>
        </select>
        <button type="submit">Add Product</button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {message && <p className="success-message">{message}</p>}
    </div>
  );
};

export default Sell;
